#!/usr/bin/env node

import { Command } from "commander";

import { cloneProject, createProject } from "./commands";

const program = new Command();

program
  .name("snapcube")
  .description("Clone or recreate project structure")
  .version("1.0.0");

program
  .command("clone")
  .argument("<dir>", "Directory to clone")
  .description("Save structure of the project to JSON")
  .option("--ignore-binaries", "Ignore files like images, pdfs, videos etc")
  .option("--ignore-all", "Ignore all files")
  .action((rootPath, options) =>
    cloneProject(rootPath, options.ignoreBinaries, options.ignoreAll)
  );

program
  .command("create")
  .argument("<json-file>", "JSON file to create project from")
  .description("Create project files from a saved JSON")
  .action(createProject);

program.parse();

import { writeFileSync } from "fs";
import { basename, resolve } from "path";

import { getProjectFiles } from "../core/getProjectFiles";

export const cloneProject = (
  rootPath: string,
  ignoreBinaries?: boolean,
  ignoreAll?: boolean
) => {
  const projectDirectoryName = basename(resolve(rootPath));

  console.log(`ğŸ“‚ Scanning project: ${projectDirectoryName}`);

  const files = getProjectFiles(rootPath, ignoreBinaries, ignoreAll);

  console.log(`âœ… Scan complete. Total files: ${files.length}`);

  writeFileSync(
    `${projectDirectoryName}.snapcube.json`,
    JSON.stringify(files, null, 4)
  );
  console.log("ğŸ’¾ Snapshot saved");
};

import { mkdirSync, readFileSync, writeFileSync } from "fs";
import { join } from "path";

export const createProject = (filePath: string) => {
  console.log(`ğŸ“‚ Restoring project from: ${filePath}`);

  const data = JSON.parse(readFileSync(filePath, "utf-8")) as SnapCubeFile[];

  for (const file of data) {
    mkdirSync(file.filePath, { recursive: true });

    const fullPath = join(file.filePath, file.fileName);

    if (file.content === null) {
      console.log(`âš ï¸ Skipping content for ignored file: ${fullPath}`);
      continue;
    }

    if (file.isBinary) {
      console.log(`ğŸ–¼ Restoring binary file: ${fullPath}`);
      writeFileSync(fullPath, Buffer.from(file.content, "base64"));
    } else {
      console.log(`âœï¸ Restoring text file: ${fullPath}`);
      writeFileSync(fullPath, file.content, "utf-8");
    }
  }

  console.log(`âœ… Project restored successfully.`);
};

const IGNORE_DIRS = ["node_modules", ".git", ".next", "out", "dist"];
const BINARY_EXTS = [".png", ".jpg", ".jpeg", ".gif", ".webp"];

export { IGNORE_DIRS, BINARY_EXTS };

import { readdirSync, readFileSync, statSync } from "fs";
import { basename, join, relative, resolve } from "path";

import { IGNORE_DIRS } from "../config";
import { isBinaryFile } from "../utils/isBinaryFile";

export const getProjectFiles = (
  rootPath: string,
  ignoreBinaries?: boolean,
  ignoreAll?: boolean
) => {
  const files: SnapCubeFile[] = [];

  const scanDir = (path: string) => {
    for (const object of readdirSync(path, { withFileTypes: true })) {
      const fullPath = join(object.parentPath, object.name);

      if (object.isDirectory()) {
        if (IGNORE_DIRS.includes(object.name)) continue;

        scanDir(fullPath);
      } else {
        const isBinary = isBinaryFile(object.name);

        let content: string | null = null;

        if (!(ignoreAll || (ignoreBinaries && isBinary)))
          content = readFileSync(fullPath).toString(
            isBinary ? "base64" : "utf-8"
          );

        files.push({
          fileName: object.name,
          filePath: join(
            basename(resolve(rootPath)),
            relative(rootPath, object.parentPath)
          ),
          content,
          isBinary,
          encoding: isBinary ? "base64" : "utf-8",
          fileSizeInBytes: statSync(fullPath).size,
        });
      }
    }
  };

  scanDir(rootPath);

  return files;
};

import { extname } from "path";
import { BINARY_EXTS } from "../config";

export const isBinaryFile = (fileName: string) =>
  BINARY_EXTS.includes(extname(fileName.toLowerCase()));

{
  // Visit https://aka.ms/tsconfig to read more about this file
  "compilerOptions": {
    // File Layout
    "rootDir": "./src",
    "outDir": "./dist",

    // Environment Settings
    // See also https://aka.ms/tsconfig/module
    "module": "nodenext",
    "target": "esnext",

    // For nodejs:
    // "lib": ["esnext"],
    "types": ["node"],
    // and npm install -D @types/node

    // Other Outputs
    "sourceMap": true,
    "declaration": true,
    "declarationMap": true,

    // Stricter Typechecking Options
    "noUncheckedIndexedAccess": true,
    "exactOptionalPropertyTypes": true,

    // Style Options
    // "noImplicitReturns": true,
    // "noImplicitOverride": true,
    // "noUnusedLocals": true,
    // "noUnusedParameters": true,
    // "noFallthroughCasesInSwitch": true,
    // "noPropertyAccessFromIndexSignature": true,

    // Recommended Options
    "strict": true,
    "jsx": "react-jsx",
    "verbatimModuleSyntax": false,
    "isolatedModules": true,
    "noUncheckedSideEffectImports": true,
    "moduleDetection": "force",
    "skipLibCheck": true
  },
  "include": ["src/**/*", "types.d.ts"]
}

interface SnapCubeFile {
  fileName: string;
  filePath: string;
  content: string | null;
  isBinary: boolean;
  encoding: "base64" | "utf-8";
  fileSizeInBytes: number;
}
